test_index=1

run_test() {
	test_binary=$1
	input1=$2
	input2=$3
	input3=$4
	echo $test_binary
	echo $input1
	echo $input2
	echo $input3
	expected_output=$5
	actual_output=$($test_binary $input1 "$input2" "$input3")

	RED='\033[0;31m'
	GREEN='\033[0;32m'
	NC='\033[0m' # No color

	if test "$expected_output" = "$actual_output"
	then
		echo -e "${GREEN}Test $test_index passed!${NC}"
	else
		echo -e "${RED}Test $test_index failed.${NC}"
		echo "Expected: $expected_output."
		echo "Actual  : $actual_output."
	fi

	test_index=$((test_index + 1))
}

binary=./encrepxor
# test 1
b64=""
input="Burning 'em, if you ain't quick and nimble
I go crazy when I hear a cymbal"
key="ICE"
expected="0b3637272a2b2e63622c2e69692a23693a2a3c6324202d623d63343c2a26226324272765272a282b2f20430a652e2c652a3124333a653e2b2027630c692b20283165286326302e27282f"
run_test $binary "$b64" "$input" "$key" $expected

